import React, { useLayoutEffect } from "react";
import { FlatList, Text, View, TouchableHighlight, Image } from "react-native";
import styles from "./styles";
import { getRecipes } from "../../data/MockDataAPI";
import {ScrollView} from "react-native-gesture-handler";

export default function RecipesListScreen(props) {
  const { navigation, route } = props;

  const item = route?.params?.category;
  const recipesArray = getRecipes(item.id);

  useLayoutEffect(() => {
    navigation.setOptions({
      title: route.params?.title,
      headerRight: () => <View />,
    });
  }, []);

  const onPressRecipe = (item) => {
    navigation.navigate("Recipe", { item });
  };

  const renderRecipes = ({ item }) => (
    <TouchableHighlight underlayColor="rgb(183 183 183 / 90%)" onPress={() => onPressRecipe(item)}>
      <View style={styles.container}>
        <Image style={styles.photo} source={{ uri: "https://www.sanalyer.com/upload/media/posts/2021-09/27/sadece-kalbi-temiz-olanlar-thor-un-cekicini-kaldirabilir_1632729274-b.jpg" }} />
        <Text style={styles.title}>{item.title} asd</Text>
        {/*<Text style={styles.category}>{getCategoryName(item.categoryId)}</Text>*/}
      </View>
    </TouchableHighlight>
  );

  return (
      <ScrollView style={styles.mainContainer}>
        <View style={{ borderBottomWidth: 0.4, marginBottom: 10, borderBottomColor: "grey" }}>
          <Image style={styles.photoIngredient} source={{ uri: "" + "https://www.sanalyer.com/upload/media/posts/2021-09/27/sadece-kalbi-temiz-olanlar-thor-un-cekicini-kaldirabilir_1632729274-b.jpg" }} />
        </View>
    <View>
      <FlatList vertical showsVerticalScrollIndicator={false} numColumns={2} data={recipesArray} renderItem={renderRecipes} keyExtractor={(item) => `${item.recipeId}`} />
    </View>
      </ScrollView>
  );
}
